import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./styles.css']
})
export class AppComponent {
  apiUrl = 'https://pg2i4ekj00.execute-api.us-east-1.amazonaws.com/dev/classify';

  isProcessing = false;
  countdown = 15;
  progressPercentage = 0;
  interval: any;

  isHuman: boolean | null = null;
  confidence = 0;
  sagemakerResult = '';
  rekognitionResult = '';
  agreement = false;

  captured = false;
  capturedImage: string | null = null;
  aiBoxes: { left: number, top: number, size: number }[] = [];

  constructor(private http: HttpClient) {}

  isCameraActive = false;
  videoElement!: HTMLVideoElement;

  captureImage() {
    if (!this.isCameraActive) {
      this.openCamera();
    } else {
      this.takePhoto();
    }
  }

  openCamera() {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        this.isCameraActive = true;
        setTimeout(() => {
          this.videoElement = document.querySelector('video')!;
          this.videoElement.srcObject = stream;
        }, 100);
      })
      .catch((error) => console.error("Camera access denied:", error));
  }

  takePhoto() {
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    if (context && this.videoElement) {
      canvas.width = this.videoElement.videoWidth;
      canvas.height = this.videoElement.videoHeight;
      context.drawImage(this.videoElement, 0, 0, canvas.width, canvas.height);
      this.capturedImage = canvas.toDataURL('image/png'); // Convert to Base64
      this.captured = true;
      this.isCameraActive = false;
      this.generateAiBoxes();
    }
  }

  generateAiBoxes() {
    // Ensure AI boxes appear **only when** an image is captured
    if (!this.captured) {
      this.aiBoxes = [];
      return;
    }

    this.aiBoxes = [];
    for (let i = 0; i < 3; i++) {
      this.aiBoxes.push({
        left: Math.random() * 70 + 15,
        top: Math.random() * 70 + 15,
        size: Math.random() * 30 + 20
      });
    }
  }

  resetCapture() {
    // Reset everything when a new capture is required
    this.captured = false;
    this.capturedImage = null;
    this.aiBoxes = [];
  }
}
