import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./styles.css']
})
export class AppComponent {
  apiUrl = 'https://your-api-gateway-url/classify'; // Replace with actual API URL

  isProcessing = false;
  countdown = 15;
  progressPercentage = 0;
  interval: any;

  isHuman: boolean | null = null;
  confidence = 0;
  sagemakerResult = '';
  rekognitionResult = '';
  agreement = false;

  captured = false;
  capturedImage: string | null = null;
  aiBoxes: { left: number, top: number, size: number }[] = [];

  constructor(private http: HttpClient) {}

  captureImage() {
    this.captured = true;
    this.capturedImage = 'assets/sample-face.jpg'; // Replace with actual image capture logic
    this.generateAiBoxes();
  }

  generateAiBoxes() {
    this.aiBoxes = [];
    for (let i = 0; i < 3; i++) {
      this.aiBoxes.push({
        left: Math.random() * 70 + 15,
        top: Math.random() * 70 + 15,
        size: Math.random() * 30 + 20
      });
    }
  }
}
<div class="container">
  <!-- Header -->
  <header class="header">
    <h1>LiveCheck AI</h1>
    <nav>
      <ul>
        <li><a href="#" class="active">üè† Home</a></li>
        <li><a href="#">üì§ Upload</a></li>
        <li><a href="#">üìä Results</a></li>
      </ul>
    </nav>
  </header>

  <!-- Upload Section -->
  <section class="upload-section">
    <h2>Upload a photo to check for liveness</h2>
    <div class="camera-box">
      <img src="assets/camera-icon.png" alt="Camera Icon" class="camera-icon" />
    </div>
    <input type="file" accept="image/*" (change)="onImageUpload($event)" />
    <button class="upload-btn">üì∑ Capture & Process</button>
  </section>

  <!-- Processing Section -->
  <section class="processing-section" *ngIf="isProcessing">
    <h2>Processing...</h2>
    <div class="progress-bar">
      <div class="progress" [style.width.%]="progressPercentage"></div>
      <span class="progress-time">{{ countdown }}s</span>
    </div>
  </section>

  <!-- Results Section -->
  <section class="results-section" *ngIf="isHuman !== null && !isProcessing">
    <h2>Analysis Results</h2>
    <ul>
      <li><span>Liveness Score:</span> <strong>{{ confidence * 100 | number:'1.0-2' }}%</strong></li>
      <li><span>Face Detection:</span> <strong [class.passed]="isHuman" [class.failed]="!isHuman">
        {{ isHuman ? 'Passed' : 'Failed' }}
      </strong></li>
      <li><span>Anti-Spoofing Check:</span> <strong [class.verified]="agreement">
        {{ agreement ? 'Verified' : 'Review Required' }}
      </strong></li>
    </ul>
    <div class="result-message" [class.success]="isHuman" [class.error]="!isHuman">
      {{ isHuman ? '‚úÖ This appears to be a live person' : '‚ùå Not a human, please retake the photo' }}
    </div>
  </section>
</div>
